import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';

const socket = io(process.env.REACT_APP_API_URL || 'http://localhost:5000');

function App() {
  const [username, setUsername] = useState('');
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [users, setUsers] = useState([]);
  const [messages, setMessages] = useState([]);
  const [messageInput, setMessageInput] = useState('');
  const messagesEndRef = useRef(null);

  useEffect(() => {
    fetchUsers();
    fetchMessages();

    socket.on('newMessage', (message) => {
      setMessages((prev) => [...prev, message]);
    });

    return () => {
      socket.off('newMessage');
    };
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const fetchUsers = async () => {
    try {
      const res = await fetch('/api/users');
      const data = await res.json();
      setUsers(data);
    } catch (err) {
      console.error('Failed to fetch users', err);
    }
  };

  const fetchMessages = async () => {
    try {
      const res = await fetch('/api/messages');
      const data = await res.json();
      setMessages(data);
    } catch (err) {
      console.error('Failed to fetch messages', err);
    }
  };

  const handleLogin = async () => {
    if (!username.trim()) return;
    try {
      const res = await fetch('/api/users', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username }),
      });
      if (res.ok) {
        const user = await res.json();
        setLoggedInUser(user);
        setUsername('');
        fetchUsers();
      } else {
        const error = await res.json();
        alert(error.error || 'Login failed');
      }
    } catch (err) {
      console.error('Login error', err);
    }
  };

  const handleSendMessage = () => {
    if (!messageInput.trim() || !loggedInUser) return;
    socket.emit('sendMessage', { senderId: loggedInUser._id, content: messageInput });
    setMessageInput('');
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  if (!loggedInUser) {
    return (
      <div style={{ padding: 20 }}>
        <h2>Login</h2>
        <input
          type="text"
          placeholder="Enter username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <button onClick={handleLogin}>Login</button>
      </div>
    );
  }

  return (
    <div style={{ maxWidth: 600, margin: '20px auto', padding: 20, border: '1px solid #ccc' }}>
      <h2>Chat App</h2>
      <div>
        <strong>Logged in as:</strong> {loggedInUser.username}
      </div>
      <div style={{ display: 'flex', marginTop: 10 }}>
        <div style={{ flex: 1, marginRight: 10 }}>
          <h3>Users</h3>
          <ul>
            {users.map((user) => (
              <li key={user._id}>{user.username}</li>
            ))}
          </ul>
        </div>
        <div style={{ flex: 3, display: 'flex', flexDirection: 'column', height: 400, border: '1px solid #ddd', padding: 10 }}>
          <div style={{ flex: 1, overflowY: 'auto', marginBottom: 10 }}>
            {messages.map((msg) => (
              <div key={msg._id} style={{ marginBottom: 5 }}>
                <strong>{msg.sender?.username || 'Unknown'}:</strong> {msg.content}
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>
          <div style={{ display: 'flex' }}>
            <input
              type="text"
              value={messageInput}
              onChange={(e) => setMessageInput(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
              style={{ flex: 1, marginRight: 10 }}
              placeholder="Type a message"
            />
            <button onClick={handleSendMessage}>Send</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
